<?php

// Fixed values
$item_price = 45.99;
$tax_rate = 0.08;
$discount = 0.10;
$discount_threshold = 50.00;
$budget = 55.00;

// Step 1: Calculate total with tax
$tax = $item_price * $tax_rate;
$total_price = $item_price + $tax;

// Step 2: Apply discount if total price is over the threshold
if ($total_price > $discount_threshold) {
    $total_price *= (1 - $discount); // Apply 10% discount
}

// Step 3: Compare with budget and show message
if ($total_price <= $budget) {
    echo "<h2 style='text-align: center;'>Dayum brah! The item is within your budget!</h2>
    <img src='data:image/jpeg;base64,' alt='Success' style='max-width: 300px; display: block; margin: 0 auto;'>";
} else {
    echo "<h2 style='text-align: center;'>Sorry Dawg, the item is over your budget.</h2>
    <img src='https://i.kym-cdn.com/entries/icons/original/000/016/546/hidethepainharold.jpg' alt='Failure' style='max-width: 300px; display: block; margin: 0 auto;'>";
}

?>
